import React from "react";

class Button extends React.Component {
    constructor() {
        super();
        this.state = {
            count: 0
        }
    }

    addCounter = () => {
        this.setState(
            {
                count: this.state.count + 1
            }
        )
        console.log("added 1")
    }

    render(){
        return (
            <div>
                <button onClick={this.addCounter}>send money</button>
                <p>counts = {this.state.count}</p>
            </div>
        )
    }
}

export default Button
